var nowUnixtime = parseInt(Date.now() / 1000);

function klevufejs_getCookie() {
    var c = "",
        ca = decodeURIComponent(document.cookie).split(';');
    for (var i = 0; i < ca.length; i++) {
        c = ca[i];
        if (typeof c !== "string") {
            continue;
        }
        while (c.charAt(0) === ' ') {
            c = c.substring(1);
        }
        if (c.indexOf("klv_mage=") === 0) {
            return JSON.parse(c.substring("klv_mage=".length, c.length));
        }
    }
    return {
        expire_sections: {}
    };
}

document.addEventListener('klevu.customerData.loaded', function (e) {
    var klevufejs_cookie = klevufejs_getCookie();
    klevufejs_cookie.expire_sections.customerData = nowUnixtime + 600;

    document.cookie = "klv_mage=" + JSON.stringify(klevufejs_cookie) + ";" + (new Date((nowUnixtime + 432000) * 1000).toUTCString()) + ";path=/;SameSite=Strict";
});

var klevufejs_cookie = klevufejs_getCookie();
var klevuData = {
    ...{
        customerData: {
            revalidate_after: -1
        }
    },
    ...JSON.parse(window.localStorage.getItem('klv_mage') || '{}')
};

const klevuCustomerDataLoadedEvent = document.createEvent('CustomEvent');
klevuCustomerDataLoadedEvent.initEvent('klevu.customerData.loaded', false, true);
const klevuCustomerDataLoadErrorEvent = document.createEvent('CustomEvent');
klevuCustomerDataLoadErrorEvent.initEvent('klevu.customerData.loadError', false, true);

if (typeof klevufejs_cookie.expire_sections !== "object"
    || (klevufejs_cookie.expire_sections.customerData || -1) < nowUnixtime
    || klevuData.customerData.revalidate_after < nowUnixtime
) {
    var xhttp = new XMLHttpRequest();
    xhttp.onerror = function (request) {
        document.dispatchEvent(klevuCustomerDataLoadErrorEvent);
    };
    xhttp.ontimeout = function (request) {
        this.onerror(request);
    };
    xhttp.onload = function (request) {
        if (this.status >= 400 || this.timeout) {
            this.onerror(request);
            return;
        }

        var klevuData = JSON.parse(window.localStorage.getItem('klv_mage') || '{}');
        klevuData.customerData = JSON.parse(this.response);
        window.localStorage.setItem('klv_mage', JSON.stringify(klevuData));

        document.dispatchEvent(klevuCustomerDataLoadedEvent);
    };
    xhttp.open('GET', 'https://www.diptyqueparis.com/en_eu/rest/V1/klevu/customerData', false);
    xhttp.send();
} else {
    document.dispatchEvent(klevuCustomerDataLoadedEvent);
}

